    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Register{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link rel=\"stylesheet\" href=\"{{ asset('build/register.css') }}\">
{% endblock %}

{% block body %}
<div class=\"register-container\">
    {% for flash_error in app.flashes('verify_email_error') %}
        <div class=\"alert alert-danger\" role=\"alert\">{{ flash_error }}</div>
    {% endfor %}

    <h2 class=\"text-center mb-4\">Sign Up</h2>

    {{ form_errors(registrationForm) }}

    {{ form_start(registrationForm) }}
        <div class=\"row\">
            <div class=\"col-md-6\">
                {{ form_row(registrationForm.firstName) }}
            </div>
            <div class=\"col-md-6\">
                {{ form_row(registrationForm.lastName) }}
            </div>
        </div>

        <div class=\"row\">
            <div class=\"col-md-4\">
                {{ form_row(registrationForm.phone) }}
            </div>
            <div class=\"col-md-8\">
                {{ form_row(registrationForm.email) }}
            </div>
        </div>
        {{ form_row(registrationForm.plainPassword, {
            label: 'Password'
        }) }}
        {{ form_row(registrationForm.agreeTerms) }}

        <div class=\"mt-3 mb-3\">
            <label>Have already a account ?</label>
            <a href=\"{{ path('app_login') }}\">Sign In</a>
        </div>

        <button type=\"submit\" class=\"btn w-100\">Sign</button>
    {{ form_end(registrationForm) }}
</div>
{% endblock %}
", "registration/register.html.twig", "/home/eny-fiti/Documents/etech/blog/backend/templates/registration/register.html.twig");
