    <article>
        <h1>{{ article.title }}</h1>
        <p>{{ article.description }}</p>
        <p class=\"text-muted\">Catégorie: {{ article.category.name }}</p>
        <hr>
    </article>

    <section class=\"mt-5\">
        <h2>Commentaires ({{ article.comments|length }})</h2>
        
        {% for comment in article.comments %}
            <div class=\"card mb-3\">
                    <p>{{ comment.content }}</p>
                </div>
            </div>
        {% else %}
            <p>Aucun commentaire pour le moment.</p>
        {% endfor %}
        
