    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Reset your password{% endblock %}

{% block body %}
<div class=\"container mt-5\">
    <div class=\"row justify-content-center\">
        <div class=\"col-lg-6 col-md-8 col-sm-10 shadow-blue\">
            <div class=\"card shadow p-4\">
                <h2 class=\"text-center mb-4\">Reset your password</h2>

                {% for flash_error in app.flashes('reset_password_error') %}
                    <div class=\"alert alert-danger\">{{ flash_error }}</div>
                {% endfor %}

                {{ form_start(requestForm) }}

                    {{ form_row(requestForm.email) }}

                    <div class=\"form-text mb-3\">
                        Enter your email address, and we will send you a link to reset your password.
                    </div>

                    <div class=\"d-flex justify-content-between mt-4\">
                        <button type=\"submit\" class=\"btn btn-primary px-4\">
                            <i class=\"bi bi-check-circle me-2\"></i> Send
                        </button>
                        <a href=\"{{ path('app_user_profile') }}\" class=\"btn btn-outline-secondary\">
                            <i class=\"bi bi-x-circle me-2\"></i> Cancel
                        </a>
                    </div>

                {{ form_end(requestForm) }}
            </div>
        </div>
    </div>
</div>
{% endblock %}
", "reset_password/request.html.twig", "/home/nyfitia/Documents/Fitia/etech/projet/blog/templates/reset_password/request.html.twig");
