    public function getSourceContext(): Source
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Mon Profil{% endblock %}

{% block body %}
<div class=\"container py-4\">
    <div class=\"row\">
        <div class=\"col-md-4\">
            <div class=\"card shadow-sm\">
                <div class=\"card-body text-center\">
                    <img src=\"{{ user.avatar ? asset('uploads/avatars/' ~ user.avatar) : asset('images/default-avatar.png') }}\" 
                         class=\"rounded-circle img-thumbnail mb-3\" 
                         width=\"150\" 
                         height=\"150\"
                         alt=\"Avatar de {{ user.firstname ?? user.email }}\">
                    
                    <h4>{{ user.firstname }} {{ user.lastname }}</h4>
                    <p class=\"text-muted\">{{ user.email }}</p>
                    
                    <div class=\"d-grid gap-2\">
                        <a href=\"{{ path('app_user_avatar_upload') }}\" class=\"btn btn-outline-primary\">
                            <i class=\"bi bi-camera\"></i> Changer de photo
                        </a>
                        <a href=\"{{ path('app_user_profile_edit') }}\" class=\"btn btn-outline-secondary\">
                            <i class=\"bi bi-pencil\"></i> Modifier le profil
                        </a>
                    </div>
                </div>
            </div>
        </div>
        
        <div class=\"col-md-8\">
            {# Section informations supplémentaires #}
        </div>
    </div>
</div>
{% endblock %}", "user_profile/index.html.twig", "/home/eny-fiti/Documents/etech/blog/templates/user_profile/index.html.twig");
